import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './HomePage.scss';
import logo from '../../assets/images/drawllab-logo.svg';
import switchtheme from '../../assets/images/switch-theme.png';

export default function HomePage() {
	// Dark/Light Mode
	const [darkTheme, setDarkTheme] = useState(getDefaultTheme())
	useEffect(() => {
		localStorage.setItem('dark', JSON.stringify(darkTheme))
	}, [darkTheme])

	function getDefaultTheme() {
		const selectedTheme = JSON.parse(localStorage.getItem('dark'))
		return selectedTheme || false
	}

    return (
        <div className={darkTheme ? 'dark-theme' : 'light-theme'}>
        <div className="home">
            <img src={logo} alt="" className="home__logo" />
            <h2>a collaborative drawing application.</h2>

            <div className="home__button-container">
                <Link to="/canvas">
                    <button className="home__canvas">
                        Start Drawing
                    </button>
                </Link>
                <Link to="/about">
                    <button className="home__about">
                        About
                    </button>
                </Link>
            </div>
            <div className="toggle-switch">
                <div onClick={() => setDarkTheme(prevTheme => !prevTheme)}>
					  <img src={switchtheme} alt="toggle dark/light mode" className="nav__icon" />
				</div></div>
            </div>
        </div>
    )
}
