import './CanvasPage.scss';
import React, { useLayoutEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import rough from 'roughjs/bundled/rough.esm';

// ----- icons for toolbar -----

import paintbrush from '../../assets/images/paintbrush.svg';
import line from '../../assets/images/draw-line.svg';
import square from '../../assets/images/rectangle.svg';
import selection from '../../assets/images/select.svg';
import home from '../../assets/images/home.png';
import about from '../../assets/images/about.png';
import deleteicon from '../../assets/images/delete.png';
import colorpicker from '../../assets/images/color-picker.svg';

// ----- Functionality for Tools -----

const generator = rough.generator();

function createElement(id, x1, y1, x2, y2, type) {
	const roughElement =
		type === "line"
			? generator.line(x1, y1, x2, y2)
			: generator.rectangle(x1, y1, x2 - x1, y2 - y1);
		// generator.circle(80, 120, 50);;
  return {id, x1, y1, x2, y2, type, roughElement};
}

const nearPoint = (x, y, x1, y1, name) => {
	return Math.abs(x - x1) < 5 && Math.abs(y - y1) < 5 ? name : null;
  };

  const onLine = (x1, y1, x2, y2, x, y, maxDistance = 1) => {
	const a = { x: x1, y: y1 };
	const b = { x: x2, y: y2 };
	const c = { x, y };
	const offset = distance(a, b) - (distance(a, c) + distance(b, c));
	return Math.abs(offset) < maxDistance ? "inside" : null;
  };

const isWithinElement = (x, y, element) => {
	const { type, x1, y1, x2, y2 } = element;
  if (type === "rectangle") {
	const topLeft = nearPoint(x, y, x1, y1, "tl");
	const topRight = nearPoint(x, y, x2, y1, "tr");
	const bottomLeft = nearPoint(x, y, x1, y2, "bl");
	const bottomRight = nearPoint(x, y, x2, y2, "br");
	const inside = x >= x1 && x <= x2 && y >= y1 && y <= y2 ? "inside" : null;
	return topLeft || topRight || bottomLeft || bottomRight || inside;
  } else {
		const a = { x: x1, y: y1 };
		const b = { x: x2, y: y2 };
		const c = { x, y };
		const offset = distance(a, b) - (distance(a, c) + distance(b, c));
		const start = nearPoint(x, y, x1, y1, "start");
		const end = nearPoint(x, y, x2, y2, "end");
		const inside = Math.abs(offset) < 1 ? "inside" : null;
		return start || end || inside;
  }
};

const distance = (a, b) => Math.sqrt(Math.pow(a.x - b.x, 2) + Math.pow(a.y-b.y, 2));

const getElementAtPosition = (x, y, elements) => {
	return elements
	  .map(element => ({ ...element, position: isWithinElement(x, y, element) }))
	  .find(element => element.position !== null);
  };

const adjustElementCoordinates = element => {
  const { type, x1, y1, x2, y2 } = element;
  if (type === "rectangle") {
    const minX = Math.min(x1, x2);
    const maxX = Math.max(x1, x2);
    const minY = Math.min(y1, y2);
    const maxY = Math.max(y1, y2);
    return { x1: minX, y1: minY, x2: maxX, y2: maxY };
  } else {
    if (x1 < x2 || (x1 === x2 && y1 < y2)) {
      return { x1, y1, x2, y2 };
    } else {
      return { x1: x2, y1: y2, x2: x1, y2: y1 };
    }
  }
};

const cursorForPosition = position => {
	switch (position) {
	  case "tl":
	  case "br":
	  case "start":
	  case "end":
		return "nwse-resize";
	  case "tr":
	  case "bl":
		return "nesw-resize";
	  default:
		return "move";
	}
  };

  const resizedCoordinates = (clientX, clientY, position, coordinates) => {
	const { x1, y1, x2, y2 } = coordinates;
	switch (position) {
	  case "tl":
	  case "start":
		return { x1: clientX, y1: clientY, x2, y2 };
	  case "tr":
		return { x1, y1: clientY, x2: clientX, y2 };
	  case "bl":
		return { x1: clientX, y1, x2, y2: clientY };
	  case "br":
	  case "end":
		return { x1, y1, x2: clientX, y2: clientY };
	  default:
		return null; 
	}
  };


// ----- PAGE -----

const CanvasPage = () => {
	// ----- Set State Hooks -----

  	const [elements, setElements] = useState([]);
	const [action, setAction] = useState("none");
	const [tool, setTool] = useState("line");
	const [selectedElement, setSelectedElement] = useState(null);


  useLayoutEffect(() => {
    const canvas = document.getElementById("canvas");
    const context = canvas.getContext("2d");
		// clears canvas on refresh 
		context.clearRect(0, 0, canvas.width, canvas.height);

    const roughCanvas = rough.canvas(canvas);

    elements.forEach(({roughElement}) => roughCanvas.draw(roughElement));
	}, [elements]);

	const updateElement = (id, x1, y1, x2, y2, type) => {
		const updatedElement = createElement(id, x1, y1, x2, y2, type);

		const elementsCopy = [...elements];
		elementsCopy[id] = updatedElement;
		setElements(elementsCopy);
	};

	const handleMouseDown = (e) => {
    const { clientX, clientY } = e;

		if (tool === "select") {
			const element = getElementAtPosition(clientX, clientY, elements)
			if (element) {
				const offsetX = clientX - element.x1;
				const offsetY = clientY - element.y1;
				setSelectedElement({...element, offsetX, offsetY})
				if (element.position === "inside") {
					setAction("moving");
				  } else {
					setAction("resizing");
				  }
			}
		} else {
			const id = elements.length;
			const element = createElement(id, clientX, clientY, clientX, clientY, tool);
			setElements((prevState) => [...prevState, element]);

			setAction("drawing");

		}
  };

	const handleMouseMove = (e) => {
		const { clientX, clientY } = e;
		if (tool === "select") {
			const element = getElementAtPosition(clientX, clientY, elements);
			e.target.style.cursor = element ? cursorForPosition(element.position) : "default";
		  }

		if (action === "drawing") {
			const index = elements.length - 1;
			const { x1, y1 } = elements[index];
			updateElement(index, x1, y1, clientX, clientY, tool);
		} else if (action === "moving") {
			const { id, x1, y1, x2, y2, type, offsetX, offsetY } = selectedElement;
			const width = x2 - x1;
			const height = y2 - y1;
			const nexX1 = clientX - offsetX;
			const nexY1 = clientY - offsetY;
			updateElement(id, nexX1, nexY1, nexX1 + width, nexY1 + height, tool);

		} else if (action === "resizing") {
			const { id, type, position, ...coordinates } = selectedElement;
			const { x1, y1, x2, y2 } = resizedCoordinates(clientX, clientY, position, coordinates);
			updateElement(id, x1, y1, x2, y2, type);
		  }
  };

	const handleMouseUp = () => {
		const index = elements.length - 1;
		const { id, type } = elements[index];
		if (action === "drawing") {
			const { x1, y1, x2, y2 } = adjustElementCoordinates(elements[index]);
			updateElement(id, x1, y1, x2, y2, type);
		}
		setAction("none");
		setSelectedElement(null);
  };

  return (
		<>
			{/* Toolbar Component */}
			<div className="toolbar">
				<input
					type="radio"
					id="colorpick"
					checked={tool === "colorpick"}
					onChange={() => setTool("colorpick")}
					className="tool"
				/>
				<label
					htmlFor="colorpick"
					className="tool__label"
				>
					<img src={colorpicker} alt="colorpick icon" className="toolbar__icon"/>
				</label>
				<input
					type="radio"
					id="paintbrush"
					checked={tool === "paintbrush"}
					onChange={() => setTool("paintbrush")}
					className="tool"
				/>
				<label
					htmlFor="paintbrush"
					className="tool__label"
				>
					<img src={paintbrush} alt="paintbrush icon" className="toolbar__icon"/>
				</label>
				<input
					type="radio"
					id="line"
					checked={tool === "line"}
					onChange={() => setTool("line")}
					className="tool"
				/>
				<label
					htmlFor="line"
					className="tool__label"
				>
					<img src={line} alt="line icon" className="toolbar__icon"/>
				</label>
				<input
					type="radio"
					id="rectangle"
					checked={tool === "rectangle"}
					onChange={() => setTool("rectangle")}
					className="tool"
				/>
				<label
					htmlFor="rectangle"
					className="tool__label"
				>
					<img src={square} alt="rectangle icon" className="toolbar__icon"/>
				</label>
				<input
					type="radio"
					id="select"
					checked={tool === "select"}
					onChange={() => setTool("select")}
					className="tool"
				/>
				<label
					htmlFor="select"
					className="tool__label"
				>
					<img src={selection} alt="selection icon" className="toolbar__icon"/>
				</label>
				<input
					type="radio"
					id="delete"
					checked={tool === "delete"}
					onChange={() => setTool("delete")}
					className="tool"
				/>
				<label
					htmlFor="delete"
					className="tool__label"
				>
					<img src={deleteicon} alt="delete icon" className="toolbar__icon"/>
				</label>
			</div>

			{/* Nav Bar Component */}

			<nav className="nav">
				<Link to="/">
					<img src={home} alt="home icon" className="nav__icon"/>
				</Link>
				<Link to="/about">
					<img src={about} alt="about icon" className="nav__icon"/>
				</Link>
			</nav>



			{/* Canvas Component */}
      <canvas
        id="canvas"
        width={window.innerWidth}
        height={window.innerHeight}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        Canvas
      </canvas>
    </>
  )
}

export default CanvasPage;
